mapper.insert:
insert into items (id, name, price,
pic, createtime, detail
)
values (#{id}, #{name}, #{price},
#{pic}, #{createtime}, #{detail}
)

mapper.update:
UPDATE seckill
set
number=number-1
where seckill_id= #{seckillId}
and start_time <=  #{killTime}
and end_time >= #{killTime}
and number>0

mapper.select:
SELECT seckill_id ,name,start_time,end_time,create_time
from seckill
where seckill_id=#{seckillId}
